	HEADER_NOT_FOUND: "<h1 style=\"color:red;\">Error 404 - File Not Found: '%path%'.</h1>",
	HEADER_NOT_ALLOWED: "<h1 style=\"color:red;\">Error 403 - Forbidden.</h1>",
	HEADER_ERROR: "<h1 style=\"color:red;\">Error 500 - General Server Error.</h1>",
	LIST: '<table>'
			+'<thead>'
